import {FaCodepen, FaStore, FaUserFriends, FaUsers} from 'react-icons/fa'
import {useContext, useEffect} from 'react'
import { useParams, Link } from 'react-router-dom'
import GithubContext from '../context/github/GithubContext'
import Spinner from '../components/layout/Spinner'



function User() {
  const {user, getUser, isLoading} = useContext(GithubContext)
  const params = useParams()

  useEffect(() => {
    getUser(params.login)
    // getUserRepo(params.login)
  }, [])

  const { name, type, avatar_url, location, bio, blog, twitter_username, login, html_url, followers, following, public_repos, public_gists, hireable} = user

  if(isLoading) {
    return <Spinner />
  }

  return (
   <>
    <div className="w-full mx-auto lg:w-10/12">
      <div className="mb-4">
        <Link to='/' className='btn btn-ghost'>Back to Search</Link>
      </div>

      .grid.grid-cols-1.xl:grid-cols-3.lg:grid-cols-3.md:grid-cols-3.
    </div>
   </>
  )
}

export default User
