import { createContext, useReducer, useParam } from 'react';
import githubReduser from './GithubReduser';

const GithubContext = createContext();

const GITHUB_URL = process.env.REACT_APP_GITHUB_URL;
const GITHUB_TOKEN = process.env.REACT_APP_GITHUB_TOKEN;

export function GithubProvider({ children }) {
  const initialState = {
    users: [],
    isLoading: false,
  };

  const [state, dispatch] = useReducer(githubReduser, initialState);


  async function searchUsers(text) {
    setIsLoading();

    const params = new URLSearchParams({q: text})

    const res = await fetch(`${GITHUB_URL}/search/users?${params}`);
    const {items}= await res.json();

    dispatch({
      type: 'GET_USERS',
      payload: items,
    });
  }



  // set isLoading
  function setIsLoading() {
    dispatch({ type: 'SET_LOADING' });
  }

  return (
    <GithubContext.Provider
      value={{ users: state.users, isLoading: state.isLoading, searchUsers }}
    >
      {children}
    </GithubContext.Provider>
  );
}

export default GithubContext;
